package ensa.ma.starsapp;


import android.os.Bundle;
import android.util.Log;
import android.view.Menu;
import android.view.MenuItem;
import androidx.appcompat.widget.SearchView;

import androidx.appcompat.app.AppCompatActivity;


import androidx.appcompat.widget.Toolbar;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

;import java.util.ArrayList;
import java.util.List;

import ensa.ma.starsapp.adapter.StarAdapter;
import ensa.ma.starsapp.beans.Star;
import ensa.ma.starsapp.service.StarService;

public class ListActivity extends AppCompatActivity {
    private List<Star> stars;
    private RecyclerView recyclerView;
    private StarAdapter starAdapter;
    private StarService service;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_list);

        // Configurer la Toolbar si vous en utilisez une
        Toolbar toolbar = findViewById(R.id.toolbar);
        setSupportActionBar(toolbar);

        // Le reste de votre configuration pour le RecyclerView...
        stars = new ArrayList<>();
        this.service = StarService.getInstance();
        init();

        recyclerView = findViewById(R.id.recycle_view);
        starAdapter = new StarAdapter(this, service.findAll());
        recyclerView.setAdapter(starAdapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
    }


    private void init() {
        service.create(new Star("Cristiano Ronaldo", "https://upload.wikimedia.org/wikipedia/commons/8/8c/Cristiano_Ronaldo_2018.jpg", 3.5f));
        service.create(new Star("Federico Valverde", "data:image/jpeg;base64,", 3));
        service.create(new Star("Toni Kross", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTr2ZvFE_zya_b66JJgwJRViiQqm3CeyyGkGw&s", 5));
        service.create(new Star("Sergio Ramos", "data:image/jpeg;base64,", 1));
        service.create(new Star("Luka Modrić", "data:image/jpeg;base64,", 5));
        service.create(new Star("Thibaut Courtois", "data:image/jpeg;base64,", 1));
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.menu, menu);
        MenuItem menuItem = menu.findItem(R.id.app_bar_search);
        SearchView searchView = (SearchView) menuItem.getActionView();
        searchView.setQueryHint("Search Here !");
        searchView.setOnQueryTextListener(new SearchView.OnQueryTextListener() {
            @Override
            public boolean onQueryTextSubmit(String query) {
                return true;
            }

            @Override
            public boolean onQueryTextChange(String query) {
                Log.d("SearchView", "Text changed: " + query);
                if (starAdapter != null) {
                    starAdapter.getFilter().filter(query);
                }
                return true;
            }
        });

        return true;
    }
}
